function color_grad(num, slider_value=1) {

  console.log(num)

  const _cut = slider_value

  // #DADADA
  const start_r = 200
  const start_g = 200
  const start_b = 200

  const mid_r = 13
  const mid_g = 13
  const mid_b = 13

  // #DD5E2C
  const end_r = 255
  const end_g = 0
  const end_b = 0

  let r
  let g
  let b
  let per

  const cut_0 = 0
  const cut_1 = _cut * 0.3
  const cut_2 = _cut * 0.5
  const cut_3 = _cut * 0.9

  if(num >= cut_0 && num < cut_1) {
    per = (num - cut_0) / (cut_1 - cut_0)
    r = Math.floor(start_r + (mid_r - start_r) * per)
    g = Math.floor(start_g + (mid_g - start_g) * per)
    b = Math.floor(start_b + (mid_b - start_b) * per)
  } else if(num >= cut_1 && num < cut_2) {
    r = mid_r
    g = mid_g
    b = mid_b
  } else if(num >= cut_2 && num < cut_3) {
    per = (num - cut_2) / (cut_3 - cut_2)
    r = Math.floor(mid_r + (end_r - mid_r) * per)
    g = Math.floor(mid_g + (end_g - mid_g) * per)
    b = Math.floor(mid_b + (end_b - mid_b) * per)
  } else if(num >= cut_3) {
    r = end_r
    g = end_g
    b = end_b
  } else {
    r = start_r
    g = start_g
    b = start_b
  }

  const hex_r = r.toString(16).padStart(2, '0')
  const hex_g = g.toString(16).padStart(2, '0')
  const hex_b = b.toString(16).padStart(2, '0')

  const hex = `#${hex_r}${hex_g}${hex_b}`

  return hex
}

function numbers_html(in_rate, size_sale_rate_color, size_sale_rate) {

  const chip_style = `
    border-radius: 20px;
    background-color: rgba(0, 0, 0, 0.1);
    color: #0D0D0D;
    padding-inline: 5px;
    padding-block: none;
    margin-right: 5px;
    display:inline-block;
    width: 55px;
    text-align: center;
  `
  const div_style = `
    display: flex;
    margin-block: 2.5px;
  `

  if(in_rate > 0) {
    return `
    <div style="${div_style}">
      <div style="${chip_style}">입고비중</div>
      <div style="color: ${color_grad(in_rate)}">${(in_rate * 100).toFixed(2)}%</div>
    </div>
    <div style="${div_style}">
      <div style="${chip_style}">판매비중</div>
      <div style="color: ${color_grad(size_sale_rate_color)}">${(size_sale_rate_color * 100).toFixed(2)}%</div>
    </div>
    <div style="${div_style}">
      <div style="${chip_style}">소진율</div>
      <div style="color: ${color_grad(size_sale_rate)}">${(size_sale_rate * 100).toFixed(2)}%</div>
    </div>
    `
  } else {
    return ``
  }
}
